mapper.select:
SELECT @ROWNUM := @ROWNUM -1 AS ROWNUM, ta.* FROM( SELECT ( CASE WHEN (DATE_FORMAT(NOW(),'%Y-%m-%d') = DATE_FORMAT(b_datetime,'%Y-%m-%d')) THEN (DATE_FORMAT(b_datetime,'%H:%i')) ELSE (DATE_FORMAT(b_datetime,'%Y-%m-%d')) END ) as bDateTime, b_idx as bIdx, b_title as bTitle, b_content as bContent, b_writer as bWriter, u_id as bId, b_category as bCategory, b_hits as bHits, b_group as bGroup, b_order as bOrder, b_depth as bDepth FROM lc_board WHERE 1 = 1
and
b_title b_content b_writer
LIKE CONCAT('%',#{search.keyword},'%')
and b_category = #{search.category}
and b_category = #{search.category} and
b_title b_content b_writer
LIKE CONCAT('%',#{search.keyword},'%')
) ta INNER JOIN (SELECT @rownum := (SELECT COUNT(*)-#{pageNum}+1 FROM lc_board)) tb ON 1=1 ORDER BY bGroup DESC, bOrder ASC LIMIT #{pageNum}, #{perPage}

mapper.update:
UPDATE 	lc_board
SET 	b_group = #{bIdx}
WHERE	b_idx	= #{bIdx}

UPDATE lc_board
SET 	b_group = #{bGroup} , b_depth = #{bDepth} +1
WHERE	b_idx = #{bIdx}

UPDATE	lc_board
SET 	b_order = b_order +1
WHERE	b_group	= #{bGroup} AND b_order >= #{bOrder} and b_idx != #{bIdx}

UPDATE 	lc_board
SET		b_hits = b_hits + 1
WHERE	b_idx = #{bIdx}

UPDATE		lc_board
SET			b_title = #{bTitle},
			b_content = #{bContent}
WHERE		b_idx = #{bIdx}
